import React, { useState, useEffect } from 'react';

import { Grid, Toolbar, AppBar, Typography } from '@material-ui/core';

import Banner from './components/Banner/Banner';
import Footer from './components/Footer/Footer';
import Places from './components/Places/Places';
import Map from './components/Map/Map';

import { getPlaceByRadius } from './services/api'

function App() {
  const [centre, setCentre] = useState({lat: 0, lng: 0});
  const [places, setPlaces] = useState([]);

  getPlaceByRadius('51.575802328718375,0.18283138525078574').then((data) => {
    console.log(data);

  })

  // useEffect((() => {
  //   getPlaceByRadius('51.575802328718375,0.18283138525078574')
  //     .then((data) => {
  //       console.log(data);
  //       setPlaces(data);
  //     })
  // }), [centre]);

  return (
    <>
      <Banner />
      <Grid container spacing={2} style={{ width: '100%' }}>
        <Grid item xs={12} md={8}>
          <Map
            centre={centre}
            setCentre={setCentre}
            places={places}
          />
        </Grid>
        <Grid item xs={12} md={4}>
          <Places />
        </Grid>
      </Grid>
      <Footer />
    </>
  );
}

export default App;
